angular.module("numberGuesser",[]).controller("numberGuesserCtrl",["$scope",function(r){r.random=Math.round(10*Math.random()*10),r.counter=0,r.checkResult=function(e){var o=angular.element(event.currentTarget);r.counter<3?(r.counter++,e==r.random?(console.log("Correct Number"),o.removeClass("blankbox").addClass("bluebox")):e>r.random?(o.removeClass("blankbox").addClass("redbox"),console.log("Greater Number")):e<r.random&&(o.removeClass("blankbox").addClass("greenbox"),console.log("Smaller Number"))):(alert("Sorry You are out of  trails!! You Lost"),location.reload())}}]).filter("range",function(){return function(r,e){e=parseInt(e);for(var o=1;o<=e;o++)r.push(o);return r}});